/* @flow strict */

import { GraphQLScalarType, GraphQLError, Kind } from 'graphql';

function coerceBuffer(value) {
  if (!(value instanceof Buffer)) {
    throw new TypeError('Field error: value is not an instance of Buffer');
  }

