sourcesContent:
import _ from 'lodash';
import config from 'config';
import httpErrors from 'http-errors';
import moment from 'moment';

import { AssociationSessionTC } from '../../schema/models/association-sessions';
import { ConnectionTC } from '../../schema/models/connections';
import { SessionTC } from '../../schema/models/sessions';
import { UserTC } from '../../schema/models/users';
import uuid from '../types/uuid';

export default async function(req, res) {
  let bitscoop = env.bitscoop;

  await env.validate('#/types/uuid4', req.query.connection_id)
      .catch(function(err) {
        throw new Error('connection_id must be a 32-character UUID4 without dashes')
      });

  await env.validate('#/types/uuid4', req.query.map_id)
      .catch(function(err) {
        throw new Error('map_id must be a 32-character UUID4 without dashes')
      });

  if (req.query.existing_connection_id) {
    await env.validate('#/types/uuid4', req.query.existing_connection_id)
        .catch(function(err) {
          throw new Error('existing_connection_id must be a 32-character UUID4 without dashes')
        });
  }

  let user = await completeLogin(req, res);

  await completeConnection(req, res, user);

  if (req.user == null) {
    res.redirect('/');
  }
  else {
    res.redirect('providers');
  }
}


async function completeLogin(req, res) {
  let bitscoop = env.bitscoop;

	if (req.user == null) {
    let $filter, promise;

    let associationId = req.cookies['login_assoc'];

    $filter = {
        token: uuid(associationId),
        connection_id: uuid(req.query.connection_id)
    };

    let count = AssociationSessionsTC.getResolver('count').resolve({
      args: {
        filter: $filter
      }
    });

    if (count === 0) {
      throw new Error('Invalid association session or association session timeout');
    }

    await AssociationSessionTC.getResolver('removeOne').resolve({
      args: {
        filter: $filter
      }
    });

    let bitscoopConnection = await bitscoop.getConnection(req.query.existing_connection_id || req.query.connection_id);

    if (bitscoopConnection == null) {
      console.log('Invalid BitScoop connection.');

      res.redirect('/');
    }

    if (!_.get(bitscoopConnection, 'auth.status.authorized', false)) {
      console.log('BitScoop Connection is not authorized. In order to use this account you must grant the requested permissions.');

      res.redirect('/');
    }

    let connection = ConnectionTC.getResolver('findOne').resolve({
      args: {
        filter: {
          remote_connection_id_string: bitscoopConnection.id
        }
      }
    });

    let user = UserTC.getResolver('findOne').resolve({
      args: {
        filter: {
          id: connection.user_id.toString('hex')
        }
      }
    });

    if (!user) {
      user = UserTC.getResolver('createOne').resolve({
        args: {
          record: {
            id: uuid(),
            joined: moment.utc().toDate()
          }
        }
      })
    }

    if (!user) {
      console.log('No user and couldn\'t create a new user.');

      res.redirect('/');
    }

    let session = await SessionTC.getResolver('createOne').resolve({
      args: {
        record: {
          id: uuid(),
          user_id_string: user._id.toString('hex'),
          persist: true
        }
      },
      projection: {
        token_string: true,
        expiration: true
      }
    });

    await UserTC.getResolver('updateOne').resolve({
      args: {
        filter: {
          id: user._id.toString('hex')
        },
        record: {
          is_active: true,
          last_login: moment().utc().toDate()
        }
      }
    });

    res.cookie(config.sessions.cookieName, session.token_string, {
        domain: config.domain,
        secure: true,
        httpOnly: true,
        expires: session.expiration
    });

    return user;

  }
  else {
		// User is already logged in
		return req.user;
	}
}

